package com.jackl.exampleimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.widget.Buttonclass MainActivity : AppCompatActivity() {    private val demo1: Button by lazy { findViewById(R.id.demo1) }    private val demo2: Button by lazy { findViewById(R.id.demo2) }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        demo1.setOnClickListener {            startActivity(Intent(this,TaskActivity::class.java))        }        demo2.setOnClickListener {            startActivity(Intent(this,TasksActivity::class.java))        }    }}